import { Link } from "react-router-dom";
//import GenderCheckbox from "./GenderCheckbox";
import { useState } from "react";
import {Button, FormControl, Input} from "@chakra-ui/react";
//import useSignup from "../../hooks/useSignup.js";
import { useToast } from '@chakra-ui/react'


const SignUp = () => {
    const [show, setShow] = useState(false);
    const [inputs, setInputs] = useState({
        name: "",
        email: "",
        password: "",
        confirmPassword: "",
        pic: null
    });
    const [loading, setLoading ] = useState(false);

    // const handleCheckboxChange = (gender) => {
    //     setInputs({ ...inputs, gender });
    // };
    const toast = useToast();
    const handleClick =()=> {
        setShow(!show);
    }
    const postDetails =(pics)=>{
        setLoading(true);
        if(inputs.pics === undefined) {
            toast({
                title: 'Picture is absent.',
                description: "Add a picture.",
                status: 'warning',
                duration: 9000,
                isClosable: true,
            })
            return;
        }
        if(pics.type === "image/jpeg" || pics.type === "image/png") {
            const data = new FormData();
            data.append("file", pics );
            data.append("upload_preset", "Prescripto" );
            data.append("cloud_name", "dvxtzwyam" );
            fetch("cloudinary://549857194891633:48dDwpeiX5f6KQa7gZhqgwlfAi8@dvxtzwyam", {
                method: post,
                body: data,
                
            });
        }
    }
    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log("inputs = ");
        //await signup(inputs);
    };
    const submitHandler =(e) => {
        e.preventDefault();
    };

    return (
            <div className='loginFormBlock'>
                <h1 className='display1'>
                    Sign Up <span className='text-blue-500'> ChatApp</span>
                </h1>

                <form onSubmit={handleSubmit}>
                    <div className='form-group'>
                        <label className='label p-2'>
                            <span className='text-base text-white'>Name</span>
                        </label>
                        <input
                            type='text'
                            placeholder='Enter full name'
                            className='form-control'
                            value={inputs.name}
                            onChange={(e) => setInputs({ ...inputs, name: e.target.value })}
                        />
                    </div>

                    <div>
                        <label className='label p-2 '>
                            <span className='text-base text-white'>Username</span>
                        </label>
                        <input
                            type='text'
                            placeholder='Enter Email-username'
                            className='form-control'
                            value={inputs.email}
                            onChange={(e) => setInputs({ ...inputs, email: e.target.value })}
                        />
                    </div>

                    <div>
                        <label className='label'>
                            <span className='text-base text-white'>Password</span>
                        </label>
                        <input
                            type='password'
                            placeholder='Enter Password'
                            className='form-control'
                            value={inputs.password}
                            onChange={(e) => setInputs({ ...inputs, password: e.target.value })}
                        />
                    </div>

                    <div>
                        <label className='label'>
                            <span className='text-base text-white'>Confirm Password</span>
                        </label>
                        <input
                            type='password'
                            placeholder='Confirm Password'
                            className='form-control'
                            value={inputs.confirmPassword}
                            onChange={(e) => setInputs({ ...inputs, confirmPassword: e.target.value })}
                        />
                    </div>

                    <FormControl>
                        <label className='label'>
                            <span className='text-base text-white'>Upload your picture</span>
                        </label>
                        <Input type="file" className='inputFile' p={1.5} accept="image/*" onChange={(e)=>postDetails(e.target.files[0])} />
                    </FormControl>
                    <div>
                        <Link to={"/login"} className='' href='#'> Already have an account? </Link>
                    </div>
                    <Button className='button' disabled={'loading'} onClick={()=>submitHandler()}>
                        {!'loading' ? <span className='loading loading-spinner'></span> : "Sign Up"}
                    </Button>

                </form>
                <Button
                    onClick={() =>
                        toast({
                            title: 'Account created.',
                            description: "We've created your account for you.",
                            status: 'success',
                            duration: 9000,
                            isClosable: true,
                        })
                    }
                >
                    Show Toast
                </Button>
            </div>
    );
};
export default SignUp;

